import React, { useState } from "react";
import { NavLink} from "react-router-dom";

const AddIssues = () => {

  const [inpval, setInpval] = useState({
    name: "",
    email: "",
    age: "",
    mobile: "",
    work: "",
    add: "",
    desc: ""
  });

  const setData = (e) => {
    console.log(e.target.value);
    const {name,value} = e.target;
    setInpval((preval) => {
      return {
        ...preval,
        [name]:value
      };
    });
  };

        const addinpData = async (e) => {
            return this.http.post("/addissues",)
         e.preventDefault();


         const { name, email, age, mobile, work, add, desc } = inpval;

         const res = await fetch("/addissues", {
         method: "POST",
         headers: {
        "Content-Type": "application/json",


      },
      body:JSON.stringify({
        name,
        email,
        age,
        mobile,
        work,
        add,
        desc
      })
    });  
            console.log(res);
           const data = res.json();
            console.log(data);

    if (res.status === 405 || !data) {
        alert("error");
        console.log("error");
    } else {
      alert("data added");
      console.log("data added");

    }
  }


  return (
    <div className="container mt-5">
      <NavLink to="/">home</NavLink>

      <NavLink to="/table">Issue</NavLink>

      <form className="mt-4" method="post" >
        <div className="row">
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputEmail1" className="form-label c">
              Name
            </label>
            <input
              type="text"
              name="name"
              value={inpval.name}
              onChange={setData}
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
            />
          </div>
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              email
            </label>
            <input
              type="email"
              name="email"
              value={inpval.email}
              onChange={setData}
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              age
            </label>
            <input
              type="text"
              name="age"
              value={inpval.age}
              onChange={setData}
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              Mobile
            </label>
            <input
              type="text"
              name="mobile"
              value={inpval.mobile}
              onChange={setData}
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              Work
            </label>
            <input
              type="text"
              name="work"
              value={inpval.work}
              onChange={setData}
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              Address
            </label>
            <input
              type="text"
              name="add"
              value={inpval.add}
              onChange={setData}
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <div className="mb-3 col-lg-12 col-md-12 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              Description of your Issue
            </label>
            <textarea
              name="desc"
              value={inpval.desc}
              onChange={setData}
              className="form-control"
            ></textarea>
          </div>
          {/* <div className="form">
                      <label htmlFor="exampleFormControlFile1 d"></label>
                      <input type="file" className="form-control-file" id="exampleFormControlFile1"/>
                    </div> */}

          <button
            type="submit"
            onClick={addinpData}
            className="btn btn-primary"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddIssues;
