import React, {useState} from 'react';

const Title = () => {


    const [user, setUser] = useState({
        name: " ", email: " ", password: " "
    })
     let name, value;
    const handleInput = (e) => {

        console.log(e); 
        name = e.target.name;
        value = e.target.value;

        setUser({...user, [name]:value});


    }
  return (
  <div>
   <div className="page-content">
		<div className="form-v5-content">
			<form className="form-detail" action="#" method="post">
				<h2>Register for GitHub</h2>
				<div className="form-row">
					<label htmlFor="full-name">Full Name</label>
					<input type="text" name="full-name" value={user.name} onChange={handleInput}  id="full-name" className="input-text" placeholder="Your Name" required/>
					<i className="fas fa-user"></i>
				</div>
				<div className="form-row">
					<label htmlFor="your-email">Your Email</label>
					<input type="text" name="your-email"  value={user.email} onChange={handleInput}  id="your-email" className="input-text" placeholder="Your Email" required pattern="[^@]+@[^@]+.[a-zA-Z]{2,6}"/>
					<i className="fas fa-envelope"></i>
				</div>
				<div className="form-row">
					<label htmlFor="password">Password</label>
					<input type="password" name="password" value={user.password} onChange={handleInput} id="password" className="input-text" placeholder="Your Password" required/>
					<i className="fas fa-lock"></i>
				</div>
				<div className="form-row-last">
					<input type="submit" name="register" className="register" value="Register"/>

				</div>
			</form>
		</div>
	</div>
  </div>
  );
};

export default Title;
