import React, { useState } from "react";
import axios from "axios";
import {ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function TextForm(props) {
  const handleUpClick = () => {
    let newText = text.toUpperCase();
    setText(newText);
    props.showAlert("Converted to uppercase!", "success");
  };

  const handleLoClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
    props.showAlert("Converted to lowercase!", "success");
  };

  const handleOnChange = (event) => {
    setText(event.target.value);
  };

  const [files, setfiles] = useState([]);
  const inputChnage = (e) => {
    setfiles(e.target.files)
  };

  const onSubmit = (e) => {
    e.preventDefault();

    const data = new FormData();


    for(let i = 0; i < files.length; i++) {
      data.append('file', files[i]);
  }

    axios.post("//localhost:3003/upload", data)
      .then((res) => {
        toast("Successfull");
      })
      .catch((e) => {
        toast("Invalid");
      });
  };

  // const handleClearClick = ()=>{
  //     let newText = '';
  //     setText(newText);
  //     props.showAlert("Text Cleared!", "success");
  // }

  // const handleCopy = () => {
  //     navigator.clipboard.writeText(text);
  //     props.showAlert("Copied to Clipboard!", "success");
  // }

  // const handleExtraSpaces = () => {
  //     let newText = text.split(/[ ]+/);
  //     setText(newText.join(" "));
  //     props.showAlert("Extra spaces removed!", "success");
  // }

  const [text, setText] = useState("");

  return (
    <>
      <h1>You Can Submit Your Issues Here!</h1>
      <div className="container my-2">
        <h2>Your text summary</h2>
        <p>
          {
            text.split(/\s+/).filter((element) => {
              return element.length !== 0;
            }).length
          }{" "}
          words and {text.length} characters
        </p>
        <p>
          {0.008 *
            text.split(/\s+/).filter((element) => {
              return element.length !== 0;
            }).length}{" "}
          Minutes read
        </p>
        <h2>Preview</h2>
        <p>{text.length > 0 ? text : "Nothing to preview!"}</p>
      </div>
      <div className="container my-5">
        <div className="row">
          <input
            className="form-control contrast my-4"
            placeholder="Title"
            aria-label="Title"
            type="text"
            name="issue[title]"
            id="issue_title"
          />
        </div>

        <div className="row">
          <textarea
            className="form-control"
            value={text}
            onChange={handleOnChange}
            id="myBox"
            rows="8"
          ></textarea>
        </div>
        <button
          disabled={text.length === 0}
          className="btn btn-dark mx-1 my-1"
          onClick={handleUpClick}
        >
          Uppercase
        </button>
        <button
          disabled={text.length === 0}
          className="btn btn-dark mx-1 my-1"
          onClick={handleLoClick}
        >
          Lowercase
        </button>
        <button disabled={text.length === 0} className="btn btn-dark mx-1 my-1">
          Submit Issues
        </button>
        {/* <button disabled={text.length===0} className="btn btn-primary mx-1 my-1" onClick={handleClearClick}>Clear Text</button>
            <button disabled={text.length===0} className="btn btn-primary mx-1 my-1" onClick={handleCopy}>Copy Text</button>
              <button disabled={text.length===0} className="btn btn-primary mx-1 my-1" onClick={handleExtraSpaces}>Extra Spaces</button> */}
      </div>

      <div className="container">
        <div className="row">
          <div className="col-md-15">
            <form method="post" action="#" id="#" onSubmit={onSubmit}>
              <div className="form-group files my-3">
                <label>Upload Your File </label>
                <input
                  type="file"
                  onChange={inputChnage}
                  className="form-control"
                  multiple
                />
              </div>

              <button className="btn btn-dark mx-1 my-1">Submit</button>
            </form>
          </div>
        </div>
      </div>
      <ToastContainer />
    </>
  );
}
