    }

    const [text, setText] = useState(''); 

    return (
        <>
        <div className="container my-5"> 
        <div className="row">
        <input className="form-control contrast my-4"  placeholder="Title" aria-label="Title" type="text" name="issue[title]" id="issue_title"/>
        </div>



             <div className="row">
            <textarea className="form-control" value={text} onChange={handleOnChange}  id="myBox" rows="8"></textarea>
