import React, {useState} from 'react';

const Title = () => {


    const [user, setUser] = useState({
        name: " ", email: " ", password: " "
    })
     let name, value;
    const handleInput = (e) => {

        name = e.target.name;

    }
  return (
  <div>
   <div class="page-content">
		<div class="form-v5-content">
			<form class="form-detail" action="#" method="post">
				<h2>Register for GitHub</h2>
				<div class="form-row">
					<label for="full-name">Full Name</label>
					<input type="text" name="full-name" value={user.name} onChahnge = {handleInput} id="full-name" class="input-text" placeholder="Your Name" required/>
					<i class="fas fa-user"></i>
				</div>
				<div class="form-row">
					<label for="your-email">Your Email</label>
					<input type="text" name="your-email"  value={user.email} onChahnge = {handleInput}  id="your-email" class="input-text" placeholder="Your Email" required pattern="[^@]+@[^@]+.[a-zA-Z]{2,6}"/>
					<i class="fas fa-envelope"></i>
				</div>
				<div class="form-row">
					<label for="password">Password</label>
					<input type="password" name="password" value={user.password} onChahnge = {handleInput} id="password" class="input-text" placeholder="Your Password" required/>
					<i class="fas fa-lock"></i>
				</div>
				<div class="form-row-last">
					<input type="submit" name="register" class="register" value="Register"/>
				</div>
			</form>
		</div>
	</div>
  </div>
  );
};

export default Title;
