import React, { useState } from "react";
import { NavLink,useHistory} from "react-router-dom";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import LogoutIcon from '@mui/icons-material/Logout';
import SkipPreviousIcon from '@mui/icons-material/SkipPrevious';



const AddIssues = () => {
  toast.configure()
    const history = useHistory();

  const [inpval, setInpval] = useState({
    name: "",
    email: "",
    age: "",
    mobile: "",
    work: "",
    add: "",
    desc: ""
  });

  const setData = (e) => {
    console.log(e.target.value);
    const {name,value} = e.target;
    setInpval((preval) => {
      return {
        ...preval,
        [name]:value
      };
    });
  };

        const addinpData = async (e) => {
         e.preventDefault();


         const { name, email, age, mobile, work, add, desc } = inpval;

         const res = await fetch("https://githubissue123.herokuapp.com/addissues", {
         method: "POST",
         headers: {
        "Content-Type": "application/json"

      },
      body:JSON.stringify({
        name,
        email,
        age,
        mobile,
        work,
        add,
        desc
      })
    });  

    //   const data = await res.json();
    //    console.log(data);
    // console.log('res==>>>',JSON.parse(res))

    const data = await res.json() ;
    console.log(data);
    if (res.status === 422 || !data) {
         toast("No Data")
        console.log("error");
    } else {

      toast("Data Added Successfully!")

      history.push('/table');

      console.log("data added");

    }
  }


  return (
    <div className="container mt-5">
      <NavLink to="/"><LogoutIcon/></NavLink>

      <NavLink className="nl" to="/table"><SkipPreviousIcon/></NavLink>

      <form className="mt-4" method="post" encType="multipart/form-data" >
        <div className="row">
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputEmail1" className="form-label c">
              Name
            </label>
            <input
              type="text"
              name="name"
              value={inpval.name}
              onChange={setData}
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
            />
          </div>
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              email
            </label>
            <input
              type="email"
              name="email"
              value={inpval.email}
              onChange={setData}
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              age
            </label>
            <input
              type="text"
              name="age"
              value={inpval.age}
              onChange={setData}
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              Mobile
            </label>
            <input
              type="text"
              name="mobile"
              value={inpval.mobile}
              onChange={setData}
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              Work
            </label>
            <input
              type="text"
              name="work"
              value={inpval.work}
              onChange={setData}
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <div className="mb-3 col-lg-6 col-md-6 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              Address
            </label>
            <input
              type="text"
              name="add"
              value={inpval.add}
              onChange={setData}
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <div className="mb-3 col-lg-12 col-md-12 col-12">
            <label htmlFor="exampleInputPassword1" className="form-label c">
              Description of your Issue
            </label>
            <textarea
              name="desc"
              value={inpval.desc}
              onChange={setData}
              className="form-control"
            ></textarea>
          </div>
          <div className="form">
                      <label htmlFor="exampleFormControlFile1 d"></label>
                      <input type="file"name="image" className="form-control-file" id="exampleFormControlFile1"/>
                    </div>

          <button
            type="submit"
            onClick={addinpData}
            className="btn btn-primary"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddIssues;
